package net.ayataka.marinetooler.proxy.websocket

import net.ayataka.marinetooler.ICE_LAKE
import net.ayataka.marinetooler.utils.fromHexToBytes
import net.ayataka.marinetooler.utils.warn
import java.io.IOException
import java.net.BindException
import java.net.ServerSocket

class PolicyServer(private val onSent: () -> Unit) : Thread() {
    private var serverSocket: ServerSocket? = null

    init {
        try {
            serverSocket = ServerSocket(443)
        } catch (ex: BindException) {
            ICE_LAKE.showError("ポリシーサーバーの初期化に失敗しました。ポート 443 が既に利用されています。")
            System.exit(1)
        } catch (ex: Exception) {
            ex.printStackTrace()
        }

        start()
        println("[WS POLICY] Started on 443.")
    }

    override fun run() {
        // Infinite loop until this thread is canceled.
        while (!Thread.interrupted()) {
            try {
                if (serverSocket != null) {
                    val socket = serverSocket?.accept()

                    val data = ("").fromHexToBytes()
                    socket?.getOutputStream()?.write(data)
                    socket?.getOutputStream()?.flush()
                    serverSocket?.close()
                    println("[WS POLICY] Stopped.")
                    onSent.invoke()
                    break
                }
            } catch (ex: InterruptedException) {
                break
            } catch (ex: IOException) {
                warn("IO ERROR occurred during connection thread initialization!")
                ex.printStackTrace()
                break
            }
        }
    }
}
